INSERT INTO `articles` (`id`, `title`, `date`, `short_desc`, `text`, `image`, `category`, `archive`) VALUES
(2, 'Hrvatska ovog vikenda dobiva novog pobjednika rukometne Lige prvaka', '2022-06-16', 'Svaki sudionik Final Foura ima po jednog Hrvata.', 'Ispred nas je vikend kad ćemo dobiti novog klupskog prvaka Europe u rukometu. Zapravo, ne nužno novog, budući da na turniru četiri najbolje momčadi nastupa i prošlogodišnji prvak. \r\n<br><br>\r\nOno što je svakako zanimljivo je da će Hrvatska još jednom dobiti igrača s najvrjednijom klupskom rukometnom titulom. Naime, sva četiri kluba sudionika imaju hrvatskog rukometaša u svojim redovima. I niti jedan tamo nije tek prolaznik nego su svi itekako bitni članovi momčadi.\r\n<br><br>\r\nU redovima branitelja naslova Barcelone nastupa Luka Cindrić. On ove sezone nije toliko efikasan u Ligi prvaka kao nekih ranijih te je do sada postigao tek 27 pogodaka, no nema sumnje da će na završnom turniru u Kölnu biti jedan od glavnih faktora Blaugrane.\r\n<br><br>\r\nBarcelone će u polufinalu igrati protiv THW Kiela, koji će ostanak bez titule prvaka Njemačke pokušati nadomjestiti novom europskom titulom, istom koju su osvojiti prije dvije godine. Tada im je jedan od ključnih igrača bio Domagoj Duvnjak, od kojeg se puno očekuje i ove godine. Kapetan hrvatske reprezentacije ove sezone postigao je 44 pogotka uz već standardan ogroman doprinos u obrani.\r\n\r\nU redovima Kielcea igrat će Igor Karačić. Ove sezone on je također sjajan s 44 pogotka i velikim brojem asistencija te je nerijetko glavni ili ključni igrač Poljaka.\r\n<br><br>\r\nKielce će svoje mjesto u finalnoj utakmici tražiti preko mađarskog Veszprema, u čijim redovima igra Manuel Štrlek. I dalje je on ponajbolji igrač mađarskog velikana, a ove sezone Lige prvaka upisao je 68 pogodaka. Veszprem je također ostao bez ligaškog naslova i to u pravoj drami.\r\n<br><br>\r\nSve u svemu, velike su šanse da Ivan Čupić više neće biti jedini rukometaš s tri titule europskog prvaka te ujedno naravno i jedini Hrvat otkako se igra Final four u Kölnu. Do sada su dvije osvajali Duvnjak, Karačić i Cindrić, tako da je šansa od 75% da će mu se jedan pridružiti.\r\n<br><br>\r\nŠto se tiče Štrkela, on za sada ima jednu, 2016. s Kielceom, ali igra u klubu koji žudi za naslovom. Naime, Veszprem je klub s najviše nastupa u finalu ili na Final four, a da naslov nikad nisu osvojili. Četiri puta gubili su finalni, a pet puta polufinalni ogled.\r\n<br><br>\r\nJe li ovo ta godina ili će prvak postati jedan od ranijih osvajača, saznat ćemo u nedjelju.\r\n\r\n', 'art3.jpg', 'Sport', 0),
(3, 'Real pretekao Barcelonu i potvrdio novog igrača cijene milijardu eura', '2022-06-16', 'Stijelac dobio bogati ugovor s Real Madridom.', 'Tri nogometaša do sada su u svom ugovoru imali klauzulom istaknutu otkupnu cijenu u iznosu od milijardu eura. To su Barcelonini dragulji Pedri i Ansu Fati te Realov napadač Karim Benzema.\r\n<br><br>\r\nTko zna, možda bi takvih slučajeva bilo i više da u drugim ligama postoji zakon kojim se takva klauzula mora staviti u ugovor. Kako je to zakon samo u Španjolskoj, apsolutno je očekivano da tu predvodnici budu dva najpopularnija i najveća kluba.\r\n<br><br>\r\nGore spomenutoj trojci sad se priključuje i četvrti igrač, čime je Real odnos takvih ugovora doveo na 2:2. Novi ugovor, koji sadrži spomenutu otkupnu klauzulu, dobio je junak osvajanja Lige prvaka, Vinicius Junior.\r\n<br><br>\r\nTo je otkrio Fabricio Romano, dodavši kako je sve dogovoreno, ali da će javno otkriveni i potpisano biti u sprnju, kad već cijeli klub bude u punom pogonu. \r\n<br><br>\r\nVinicius dobiva i novu plaću kojom ulazi u skupinu najvećih i najplaćenijih zvijezda kluba. Riječ je o ugovoru do 2026. godine. 21-godišnjak je do sada s prvakom Europe, kojem je pristupio u ljeto 2018. godine, bio vezan do 2024.\r\n<br><br>\r\nReal je tako zapravo pretekao Barcelonu, koja već par tjedana na stolu ima ugovor s takvom klauzulom, koji je ponuđen njihovom trećem mladom dragulju. Radi se naravno o Gaviju, no on još uvijek nije isti potpisao, iako se pregovori vode na svakodnevnoj bazi.\r\n<br><br>\r\nNije poznato gdje je točno problem, ali vjeruje se da Gavijev agent Ivan De La Pena pokušava iskoristiti veliki interes drugih elitnih klubova da bi svom igraču osigurao što bolje uvjete.\r\n<br><br>\r\nItekako je jasno da Gavi želi ostati u Blaugrani, kao i to da ga oni pod svaku cijenu žele zadržati. Tamo se zapravo događa tek cjenkanje, sve dok se obje strane ne nađu na nekoj sredini.\r\n\r\n', 'art4.jpg', 'Sport', 0),
(4, 'Premijer Plenković: \"Split je postao žrtva politikantstva, žrtva ruganja biračima. Ove afere sramote grad\"', '2022-06-16', 'Svečanosti obilježavanja Dana Sveučilišta u Splitu', 'Premijer Andrej Plenković kazao je u srijedu u Splitu kako se Sveučilište dobro razvija. Pružio je potporu HDZ-ovu kandidatu za gradonačelnika, Zoranu Đogašu.\r\n<br><br>\r\n\"Split je postao žrtva politikantstva, žrtva ruganja biračima\", rekao je premijer.\r\n<br><br>\r\n\"Vrijeme je da ljudi dobro razmisle i poklone povjerenje Zoranu Đogašu\", kazao je te dodao kako će njihov kandidat civilizirano voditi grad.\r\n<br><br>\r\n\"Imaš većinu, rušiš sam sebe, rušiš vijeće, a godinu dana nisi napravio ništa. Imamo i sve ove afere koje doslovno sramote grad zbog ljudi koji nisu dorasli poslu za koji su, nažalost, prije godinu dana dobili povjerenje\", poručio je premijer te dodao kako je sada prilika da građani kažu ne onima koji su nasilni prema novinarima, koji su sami sposobni ući u ovakve rabote.\r\n<br><br>\r\n\"Ako nekome do neki dan nije bilo jasno tko je kakav, sad je jasno\", zaključio je.\r\n<br><br>\r\nOsvrnuo se na lokalnu vlast u Novskoj koja je dobila potporu veću od 82 posto, a usto i ministra.\r\n<br><br>\r\n\"Plasirate mi tezu političke tajnice SDP-a Ahmetović. Ako ima nevidljive ruke o kojoj sanja Milanović, da mi utječemo na DORH, valjda bi ta ruka rekla da ne pokrećemo drugu temu. Imamo briljantnu temu u Splitu. Što bi bilo da se ovo Puljkovim zamjenicima dogodilo ljudima u HDZ-u? Kakva bi bila situacija? Jednom i zauvijek - DORH i policija su samostalni i rade svoj posao bez pritisaka izvršne vlasti\", poručio je Plenković.\r\n<br><br>\r\nNa pitanje da usporedi sukob interesa ministra obrane Marija Banožića s Puljkovim slučajem, Plenković je rekao kako je Banožić platio kaznu i u redu je da bude ministar.\r\n<br><br>\r\n\"Neću slušati Milanovićeve želje, budite ozbiljni\", kazao je.\r\n<br><br>\r\nNi slučaj KBC-a Zagreb, kao ni Antu Ćorušića nije htio komentirati. Samo je dodao kako je Ćorušić sam sve objasnio.\r\n<br><br>\r\nNije se osvrnuo ni na uhićenje Dumbovića, samo je kratko ustvrdio:\r\n<br><br>\r\n\"Pratim sve kao i vi\".\r\n<br><br>\r\nNa pitanje o policijskoj intervenciji u Desincu, premijer je kazao kako ne zna što se događa. \r\n<br><br>\r\n\"Pratim ono što pratite i vi, ali mislim da je jasno da ne može doći do toga da više ljudi napada policiju. Nije valjda hrvatska policija ničim izazvana dovedena situaciju da se mora braniti\", poručio je premijer te dodao kako je i Božinović u Splitu, pa novinari mogu i njemu postaviti pitanje.\r\n<br><br>\r\n\"Ono što je ključ, poduzeli smo jako puno mjera do sada, smanjili i poreze te niz drugih mjera i činit ćemo to i dalje. Ono što je u ovom trenu bitno je da promatramo situaciju koja nije samo vezana za Hrvatsku\", rekao je premijer o rastu inflacije te najavio paket mjera koje će biti donesene \"onda kad budu potrebne\".\r\n<br><br>\r\nPlenković je dodaa i da je ruska agresija na Ukrajinu dovela do rasta cijena i inflatornih pritisaka.\r\n<br><br>\r\n\"Jako je važno da raste BDP i da je nezaposlenost manja nego ikada\", naglasio je predsjednik Vlade.\r\n<br><br>\r\nNa pitanje kakva je sudbina škverana, premijer je odgovorio da se sve analizira.\r\n\r\n', 'art5.jpg', 'Politika', 0),
(5, 'Prošlo je 50 godina, a prema njemu se i dalje sude svi ostali skandali: No, je li Trump \'\'podigao\'\' ljestvicu?', '2022-06-16', 'Pedeset godina otkako je zapalila Washington', 'Ipak, neki povjesničari vjeruju da je njegov arhitekt Richard Nixon izložen riziku da ga kao primjer predsjedničke korupcije istisne Donald Trump zbog njegove uloge u napadu na američki Kapitol 2021.\r\n<br><br>\r\nNixonov osnovni zločin bio je prikrivanje provale u sjedište Demokratskog nacionalnog odbora u washingtonskom kompleksu Watergate kako bi se ukrali dokumenti koji su mu mogli pomoći na izborima na kojima bi u konačnici ionako pobijedio.\r\n<br><br>\r\nOptužbe protiv Trumpa da je potaknuo smrtonosne nerede kako bi ugrozio miran prijenos vlasti, u okviru zavjere za poništavanje rezultata predsjedničkih izbora na kojima je pobijedio Joe Biden, izgledaju \"daleko ozbiljnije\", kaže profesor povijesti Michael Green.\r\n<br><br>\r\n\"Iskreno govoreći, Nixon je već bio srušen s pijedestala\", rekao je za AFP Green sa Sveučilišta Nevada u Las Vegasu. \"Jedna od ironija je da Nixon nije trebao narediti provalu da bi pobijedio na izborima\", rekao je.\r\n<br><br>\r\n\"I nema dokaza, čak ni uz sve snimke, da je ikada bilo rasprave ili razmišljanja o poništavanju rezultata ako budu protiv njega.\"\r\n<br><br>\r\nPet provalnika iz Watergatea uhvaćeno je na djelu 17. lipnja 1972. i brzo se pokazalo da su neki povezani s kampanjom Nixona i Bijelom kućom.\r\n<br><br>\r\nIstraga koja je uslijedila na kraju je otvorila Pandorinu kutiju zloporaba i prljavih trikova koji su uključivali političko špijuniranje, krivotvorenje korespondencije, pa čak i krađu para cipela kako bi se uzdrmao Nixonov rival.\r\n<br><br>\r\nAli zataškavanje je u početku bilo toliko uspješno da je Nixon osvojio 49 od 50 država u svojoj uvjerljivoj pobjedi nad demokratom Georgeom McGovernom na predsjedničkim izborima te godine.\r\n<br><br>\r\nPrikrivanje je moglo i uspjeti da nije bilo slučajnog otkrića u ljeto 1973. da je predsjednik potajno snimao sve svoje sastanke u Bijeloj kući. To je uključilo i presudnu snimku na kojoj se moglo čuti kako Nixon naređuje da se FBI-u, koji je trebao istražiti provalu u Watergate, kaže da se \"kloni toga\".\r\n<br><br>\r\nNixon je dao ostavku nakon što je izaslanstvo republikanskih čelnika, predvođeno ultrakonzervativnim Barryjem Goldwaterom, došlo u Bijelu kuću 1974. kako bi mu reklo da će vjerojatno biti opozvan i da su sve karte otkrivene.\r\n<br><br>\r\nNa kraju je pomilovan, ali mnogi od njegovih najbližih suradnika završili su u zatvoru. Carl Bernstein i Bob Woodward, novinari koji su odigrali ključnu ulogu u svrgavanju Nixona, napisali su novi predgovor za svoju kultnu knjigu \"Svi predsjednikovi ljudi\" povlačeći paralele s Trumpom.\r\n<br><br>\r\nNjihova usporedba nudi uvid u par autsajdera koji su se osjećali opkoljeni neprijateljima u medijima i državnim institucijama. No, oni sugeriraju da Trumpovo poticanje masa na marš na Kapitol predstavlja \"obmanu koja je nadmašila čak i Nixonovu maštu\".\r\n<br><br>\r\n\"Po pravnoj definiciji ovo je očito pobuna... tako je Trump postao prvi pobunjenički predsjednik u našoj povijesti\", kažu.\r\n<br><br>\r\nAnalitičari s kojima je razgovarao AFP ukazali su na znatno drukčiji politički i medijski krajolik s kojim su se Nixon i Trump suočili kada su u pitanju posljedice njihovih postupaka.\r\n<br><br>\r\nIntervencija Goldwatera, na primjer, bila bi nezamisliva među velikom većinom današnjih republikanskih političara, koji su čvrsto uz Trumpa kroz dva opoziva i brojne druge kontroverze.', 'art6.jpg', 'Politika', 0),
(6, 'Europski čelnici sastali se sa Zelenskim u Kijevu prvi put od početka rata: \"Ukrajina će dobiti još oružja\"', '2022-06-16', 'Čelnici Francuske, Njemačke, Italije i Rumunjske', 'U prvom takvom posjetu Kijevu od početka ruske invazije 24. veljače, francuski predsjednik Emmanuel Macron, njemački kancelar Olaf Scholz, talijanski premijer Mario Draghi i rumunjski predsjednik Klaus Iohannis rukovali su se sa Zelenskim koji nije napustio Ukrajinu otkako je počeo rat.\r\n<br><br>\r\nSastali su se pošto su europski čelnici obišli ruševine zgrada u obližnjem Irpinu, poprištu teških borbi na početku rata.\r\n<br><br>\r\nScholz je ocijenio da je riječ o \"nezamislivoj okrutnosti\" i \"bezumnom nasilju\".\r\n<br><br>\r\n\"Ovo je herojski grad... obilježen stigmom barbarizma\", rekao je Macron.\r\n<br><br>\r\nKijev optužuje Francusku, Njemačku i u manjoj mjeri Italiju da sporo šalju oružje Ukrajini i da su vlastiti prosperitet stavili ispred ukrajinske slobode i sigurnosti.\r\n<br><br>\r\nMacron je s tim u vezi naglasio da šalju oružje i financijsku pomoć.\r\n<br><br>\r\n\"Francuska i Europa stoje uz Ukrajinu i njezine stanovnike od početka\", rekao je.\r\n<br><br>\r\nZelenski nije bio s njima u Irpinu.\r\n<br><br>\r\nNakon razgovora s gradonačelnikom Irpina i drugim dužnosnicima, Draghi je rekao: \"Oni su puno govorili o obnovi. Riječi boli, nade, ali i o tome što žele učiniti u budućnosti\".\r\n<br><br>\r\nKremlj je poručio da bi europski čelnici sa Zelenskim trebali \"realno sagledati stvari\", a ne razgovarati o slanju oružja.\r\n<br><br>\r\nDmitrij Medvedev, zamjenik predsjednika ruskog Vijeća za sigurnost, ismijao je njihov posjet ocijenivši da od njega \"nema nikakve koristi\".\r\n<br><br>\r\nRekao je da su \"Ukrajini obećali članstvo u EU-u i stare haubice\".\r\n\r\n', 'art7.jpg', 'Politika', 0),
(7, 'Ruski pregovarač tvrdi da je Moskva predala nacrt primirja Ukrajini', '2022-06-16', 'Putin: \'Doveli su proces u slijepu ulicu\'', 'Rusko-ukrajinski nacrt mirovnog sporazuma bio je 75 posto dogovoren u travnju, kaže ruski izaslanik. Rusija je predala dokument ukrajinskoj strani 15. travnja, podsjetio je Vladimir Medinski.\r\n<br><br>\r\nNacrt mirovnog sporazuma između Ukrajine i Rusije bio je 75 posto dogovoren u travnju, rekao je danas pomoćnik ruskog predsjednika, šef ruskog izaslanstva na razgovorima s ukrajinskom stranom Vladimir Medinski na marginama Međunarodnog ekonomskog foruma u Sankt Peterburgu (SPIEF).\r\n<br><br>\r\n\"Dugo smo vodili intenzivne razgovore s ukrajinskom stranom o uvjetima postkriznog rješenja. Ovi pregovori su okončani 15. travnja kada smo ukrajinskoj strani predali nacrt ugovora koji su dogovorile pregovaračke skupine. Dogovorili smo se oko 75 posto nacrta, rekao je Medinski novinarima.\r\n<br><br>\r\nPregovori Rusije i Ukrajine traju od 28. veljače. U Bjelorusiji je održano nekoliko sastanaka, nakon čega su strane nastavili razgovore putem video veze. U Istanbulu, Turska, 29. ožujka održana je runda pregovora licem u lice.\r\n<br><br>\r\nRuski predsjednik Vladimir Putin rekao je novinarima 12. travnja da je Kijev odstupio od sporazuma postignutih u Turskoj i doveo ovaj proces u slijepu ulicu. Glasnogovornik Kremlja Dmitrij Peskov rekao je 20. travnja da je Moskva Kijevu predala svoj nacrt dokumenta o sporazumima s jasnim formulacijama i očekuje odgovor. Ruska strana također tvrdi da je obustavljanje pregovora potpuno ukrajinska inicijativa.', 'art8.jpg', 'Politika', 0),
(8, 'Grlić Radman: Sve ide u smjeru otvaranja pregovora sa Skopljem i Tiranom', '2022-06-16', 'Na Ohrid su stigli i predsjednik Europskog vijeća.', 'Ministar vanjskih i europskih poslova Gordan Grlić Radman u četvrtak je na Ohridu rekao kako „sve ide u tom smjeru“ da se pokrene pregovarački proces za članstvo Sjeverne Makedonije i Albanije u Europskoj uniji.  Grlić Radman sudjeluje na Prespa Forumu na kojem, između ostalih, participiraju čelnici zapadnog Balkana i visoki dužnosnici Europske unije. Ministar je rekao da se na tom skupu vode razgovori i da „sve ide u tom smjeru“ da se „konačno krene s pristupnim pregovorima“ o članstvu Sjeverne Makedonije i Albanije. \r\n<br><br>\r\nGrlić Radman vjeruje kako ima dovoljno vremena da se dogovor postigne do sastanka na vrhu između Europske unije i zapadnog Balkana koji se održava za tjedan dana. \r\n<br><br>\r\nNa Ohrid su stigli i predsjednik Europskog vijeća Charles Michel te povjerenik Europske komisije za proširenje Oliver Várhelyi, no nisu najavljeni predstavnici Bugarske koja je blokirala otvaranje pregovora sa Sjevernom Makedonijom radi identitetskih pitanja. Time je blokiran put i Albaniji jer ih EU tretira u paketu.  Grlić Radman je rekao da je proširenje EU-a strateški interes Hrvatske i EU-a te da taj savez mora koristiti sve svoje pravne mehanizme za reforme u zemljama kandidatima kako bi što prije ostvarile europsku perspektivu.', 'art9.jpeg', 'Politika', 0),
(9, '\"Podržat ću dokument uz ove dvije izmjene\": Čović objavio uvjete za potpis', '2022-06-16', 'Predsjednik HDZ-a BiH Dragan Čović najavio.', '\"Predsjednik Dragan Čović će podržati jučerašnju izjavu s dodatkom dvaju malih ispravaka, koji ne bi trebali predstavljati problem za uključivanje u izjavu\", navodi se iz krugova bliskih Čoviću. \"Istodobno, to bi za nas bilo od iznimne važnosti, a dalo bi dodatnu vrijednost samom tekstu”. \r\n<br><br>\r\nČelnici 12 bošnjačkih i srpskih stranaka složili su se u nedjelju navečer nakon sastanka s predsjednikom Europskog vijeća Charlesom Michelom o sadržaju dokumenta pod nazivom “Politički sporazum o načelima za osiguranje funkcionalne Bosne i Hercegovine koja napreduje na europskom putu”.\r\n<br><br>\r\nMichel je u nedjelju pozvao čelnike 15 parlamentarnih stranaka iz Bosne i Hercegovine i tri člana predsjedništva. Na sastanak nisu došli Milan Šarović iz SDS-a i Fahrudin Radončić iz SBB-a. Predsjednik HDZ-a BiH, jedine hrvatske stranke u Parlamentarnoj skupštini BiH, Dragan Čović, koji je doputovao u Bruxelles, nije sudjelovao na sastanku.\r\n<br><br>\r\nRekao je da je \"prije dva, tri dana saznao da će sastanak biti u nekom čudnom formatu, devet bošnjačkih stranaka, pet ili šest srpskih i HDZ BiH kao jedina hrvatska stranka. Uz to je rečeno da će biti i tri člana predsjedništva”, rekao je ranije Čović.\r\n<br><br>\r\nU preambuli se kaže da čelnici parlamentarnih stranaka u BiH ponavljaju svoju predanost “očuvanju načela vladavine prava i provedbi slobodnih i demokratskih izbora kao i razvoju demokratskih institucija na svim razinama vlasti”.\r\n<br><br>\r\nČović traži da se na tu rečenicu u preambuli teksta doda: \"u skladu sa sporazumom o provedbi presuda Europskog suda za ljudska prava potpisanog u Bruxellesu 1. listopada 2013. i u skladu s političkim dogovorom o dopuni izbornog zakonodavstva potpisanog u Mostaru 17. lipnja 2020.\"\r\n<br><br>\r\nČović i predsjednik SDA Bakir Izetbegović potpisali su 17. lipnja 2020. politički sporazum kojim je usuglašen način izbora vijećnika i broj vijećnika koji se bira po gradskim područjima Mostara, kao i promjene Statuta Grada Mostara.\r\n<br><br>\r\nTaj je sporazum omogućio održavanje izbora u Mostaru koji nisu bili održani 12 godina.\r\n<br><br>\r\nU njemu su se potpisnici obvezali da će se promjene Izbornog zakona BiH temeljiti na \"legitimnom izboru i legitimnom političkom predstavljanju\" konstitutivnih naroda i građana na svim administrativno-političkim razinama, u Predsjedništvu BiH i Domovima naroda.\r\n<br><br>\r\nČović traži i promjene u paragrafu 11 usuglašenog dokumenta, koji kaže da se čelnici slažu da će unutar šest mjeseci od formiranja vlasti na svim razinama usvojiti izborne i ograničene ustavne reforme koje trebaju biti usklađene s presudama Europskog suda za ljudska prava, Ustavnog suda BiH, s preporukama OESS/ODIHR-a i GRECO-a.\r\n<br><br>\r\nČović predlaže promjenu toga paragrafa, da se u njega ubaci rečenica koja kaže da se \"izborne reforme koje će osigurati provedbu izbornih rezultata u vezi s Domovima naroda trebaju hitno usvojiti u skladu sa sporazumima spomenutim u preambuli\", to jest sa sporazumom o provedbi presuda Europskog suda za ljudska prava potpisanog u Bruxellesu 1. listopada 2013. i u skladu s političkim dogovorom o dopuni izbornog zakonodavstva potpisanog u Mostaru 17. lipnja 2020.\r\n<br><br>\r\nČović je u nedjelju navečer u Bruxellesu u izjavi novinarima izrazio sumnju u uspostavu vlasti nakon izbora s obzirom da je ključna odredba izbornog zakona ukinuta.\r\n<br><br>\r\n“Ali, ovdje je ključno pitanje kako ćete uspostaviti vlast ako nemate ključne odredbe koju je ukinuo Ustavni sud i stavio izvan snage”, kaže Čović.\r\n<br><br>\r\nUstavni sud BiH ukinuo je odredbu po kojoj se u nacionalne klubove Doma naroda Federacije BiH iz svake županije mora izabrati najmanje po jedan pripadnik svakog od tri konstitutivna naroda.\r\n<br><br>\r\nTo je omogućavalo bošnjačkim strankama da iz kantona u kojima gotovo nema Hrvata izaberu delegate koji se lažno izjasne Hrvatima i postanu članom hrvatskog nacionalnog kluba. Nacionalni klubovi u Domu naroda Federacije BiH broje po 17 članova i hrvatski klub bi bio potpuno blokiran ako Bošnjaci uspiju nametnuti svojih šest zastupnika, jer je za odluke potrebna dvotrećinska većina.\r\n<br><br>\r\nSudionici sastanka u Bruxellesu u ponedjeljak su po povratku u BiH iz Bruxellesa istaknuli da nikakav dokument nije potpisan nego da su dali suglasnost na dokument koji je objavio predsjednik Europskog vijeća Charles Michel.\r\n\r\n', 'art10.jpg', 'Politika', 0),
(10, 'Tomislav Sokol: Europska sredstva našim građanima će osigurati zdravstvo na EU razini', '2022-06-16', 'Ulaganje u zdravstvo putem kohezijskih sredstava', 'Na sjednici Odbora za regionalni razvoj (REGI) Europskog parlamenta u Bruxellesu raspravljalo se o primjeni i učincima kohezijske politike. Zastupnik u Europskom parlamentu doc. dr. sc. Tomislav Sokol istaknuo je važnost kohezijskih ulaganja u zdravstvenu zaštitu i bolju kvalitetu liječenja s ciljem postizanja jednake kvalitete liječenja u svim dijelovima Hrvatske i EU.\r\n<br><br>\r\n„Da bismo osigurali bolji i učinkovitiji zdravstveni sustav te podigli zdravstveni standard u Hrvatskoj potrebno je kohezijska sredstva ulagati u obnovu i modernizaciju infrastrukture, uključujući najsuvremeniju opremu da bi se poboljšalo pružanje zdravstvenih usluga. Također, nužno je ulagati u povećanje broja zdravstvenih radnika te njihovo zadržavanje u Hrvatskoj, digitalizaciju našeg zdravstvenog sustava te prevenciju bolesti“ – naglasio je zastupnik Sokol.\r\n<br><br>\r\nPodsjetimo, kohezijska politika predstavlja snažan instrument Europske unije za promicanje i podupiranje ukupnog skladnog razvoja njezinih država članica i regija, a znatan udio aktivnosti i proračuna EU posvećuje se upravo smanjenju razlika među regijama, s posebnim naglaskom na ruralna područja, područja zahvaćena industrijskom tranzicijom i regijama suočenima s teškim i trajnim demografskim poteškoćama.\r\n<br><br>\r\n„Kohezijska politika jedna je od temeljnih investicijskih politika EU te svojim fondovima doprinosi jačanju ekonomske, socijalne i teritorijalne kohezije Unije, promicanju rasta i zapošljavanja, posebno u regijama koje zaostaju u razvoju. U tu svrhu koristi se oko trećine proračuna na razini Unije što čini kohezijsku politiku ne samo najvažnijom investicijskom politikom EU-a, već i izrazom solidarnosti Europske unije i država članica. Upravo zbog toga, ulaganje u zdravstvo putem kohezijskih sredstava predstavlja važan faktor za regionalni razvoj i konkurentnost u Hrvatskoj, ali i u cijeloj EU“ – rekao je zastupnik Sokol.\r\n<br><br>\r\n„Nužno je da uz pomoć europskih sredstava nastavimo jačati zdravstveni sektor u Hrvatkoj, kako bi naši građani imali iste mogućnosti i standarde prilikom liječenja kao i svi ostali građani u EU“ – zaključio je zastupnik Sokol.', 'art11.jpg', 'Politika', 0),
(11, 'Bona Sosa će svoj problem pokušati riješiti kod \"čudotvorca\" koji je izliječio mnoge zvijezde Bundeslige', '2022-06-16', 'Hrvatski reprezentativac Borna Sosa', 'Hrvatski reprezentativac i zvijezda Stuttgarta Borna Sosa nije imao previše posla na posljednjem okupljanju Vatrenih za Ligu nacija. Odigrao je samo uvodnih 45 minuta protiv Austrije i to je bilo to, ali ne zato što nije želio ili što ga Dalić nije trebao, nego zato što nije mogao.\r\n<br><br>\r\nProblem je bila stara ozljeda prepone s kojom se muči već mjesecima. Preciznije, ima probleme još od ožujka, a odnedavno se situacija pogoršala je jedini način kako može igrati jest pod injekcijama ili pod lijekovima protiv bolova.\r\n<br><br>\r\nZbog toga je posljednjih tjedana drastično smanjio opterećenje pod kojim trenira kako ne bi dodatno opteretio problematično područje pokraj stidne kosti.\r\n<br><br>\r\nTaj ga je problem ponovo udaljio od veće minutaže u nacionalnoj momčadi, Dalić ga je planirao češće koristiti od spomenutih 45 minuta protiv Austrije, ali iako je najavljivao mogućnost njegovog povratka eventualno za posljednju utakmicu u lipanjskom mini ciklusu Lige nacija, onoj protiv Francuza na Stade de Franceu, čini se kako su bolovi bili previše.\r\n<br><br>\r\nSosa se sad nada kako bi njegovom problemu mogao stati na kraj „čudotvorac iz Schilerseea“, odnosno poznati liječnik kojem se za pomoć obraćaju mnoge bundesligaške zvijezde.\r\n<br><br>\r\nBild prenosi kako će pomoć potražiti kod dr. Give Thierry Grumpyja, Franvuza koji svoju bazu operacija ima u Haushamu na Schilerseeu, liječnik je fizikalne rehabilitacijske medicine, a od poznatih nogometaša kod njega su se liječili Serge Gnabry, Mario Götze i Marco Reus.\r\n\r\n', 'art12.jpg', 'Sport', 0),
(12, 'Majica najboljeg hrvatskog tenisača izazvala pažnju na turniru u Velikoj Britaniji', '2022-06-16', 'Marin Čilić na turniru u Queensu.', 'Prvi hrvatski reket Marin Čilić prebacio se na travnatu sezonu poslije velikog uspjeha u Roland Garrosu i nastavlja igrati u sjajnoj formi i u Queen\'su.\r\n<br><br>\r\nPobijedivši kazahstanskog tenisača Aleksandera Bublika u dva seta plasirao se među najboljih osam na turniru u Qeen\'s Clubu deveti put u svojoj karijeri, međutim njegove rezultate zasjenila je majica koju je nosio.\r\n<br><br>\r\nČilić je nastupio u majici koja je imala bijelu, plavu i crvenu na sebi, što je mnoge fanove podsjetilo na boje ruske zastave. Rusija je zbog rata s Ukrajinom na crnoj listi u Velikoj Britaniji, a njihovim i bjeloruskim tenisačima zabranjeno je nastupati na Otoku. Nema ih na ovom turniru, a neće ih biti ni u Wimbledonu.\r\n<br><br>\r\nZa plasman u finale Queensa Marin će igrati protiv finskog tenisača Emila Ruusuvuorija, prođe li borit će se za svoj treći naslov na ovom turniru, prvi je osvojio 2012. godine, a drugi prije četiri godine.\r\n\r\n', 'art13.jpg', 'Sport', 0),
(13, 'Luis Suarez ima novi klub, formu za Svjetsko prvenstvo brusit će u velikanu', '2022-06-16', 'Luis Suarez novi je član jedan od najvećih klubova', 'Urugvajski napadač 35-godišnji Luis Suarez karijeru će nastaviti u argentinskom velikanu River Plateu, objavili su španjolski i argentinski mediji.\r\n<br><br>\r\nSuarezu će 30. lipnja isteći ugovor s Atletico Madridom i na kultni stadion Monumental doći će kao slobodan igrač, bez odštete.\r\n<br><br>\r\nUrugvajski nogometaš već se dogovorio o uvjetima suradnje sa Riverom, gdje će zamijeniti talentiranog 22-godišnjeg Juliana Alvareza, koji bi karijeru trebao nastaviti u Manchester Cityju.\r\n<br><br>\r\nSuarez je samo jednu sezonu proveo u Južnoj Americi, na početku karijere u Nacionalu iz Montevidea. Nakon toga je preselio u Europu i igrao je za Groningen, Ajax, Liverpool, Barcelonu i Atletico Madrid.\r\nZa urugvajsku reprezentaciju odigrao je 132 utakmice i postigao je 68 golova.\r\n<br><br>\r\nUpravo reprezentacija je glavna motivacija Suarezu, koji je već najavio kako će biti spreman za Svjetsko prvenstvo, iako je lipanjsko okupljanje propustio da bi se posvetio pronalasku novog kluba.\r\n<br><br>\r\nNjegov Urugvaj inače od dolaska novog izbornika igra fantastično i mnogi ih već sad smatraju \"dark horseom\" Svjetskog prvenstva.\r\n<br><br>\r\nŠto se tiče Suareza, on Atletico napušta vrlo zadovoljan. Iako mu je druga sezona bila nešto slabija, u prvoj je odigrao ključnu rolu pri osvajanju titule prvaka Španjolske, što je njemu bilo posebno emotivno jer je iz Barcelone kao tada 33-godišnjak praktički otjeran.', 'art14.jpg', 'Sport', 0),
(14, 'Modrić otkrio favorite SP', '2022-06-16', 'Argentina bi prema Luki Modriću trebala ući u najuži krug favorita.\r\n\r\n', 'Kapetan hrvatske nogometne reprezentacije Luka Modrić rekao je u razgovoru za američki ESPN da je Argentina uvijek jedan od favorita za osvajanje Svjetskog prvenstva koje počinje za pet mjeseci.\r\n<br><br>\r\n\"Argentina, s Messijem, uvijek je jedan od favorita za osvajanje Svjetskog prvenstva\", izjavio je 36-godišnji Modrić upitan o Argentini. Podsjetio je, međutim, da je upravo Hrvatska na prošlom Svjetskom prvenstvu godine 2018. pobijedila Argentinu 3:0 u skupini.\r\n<br><br>\r\n\"Igrali smo protiv Argentine na posljednjem prvenstvu i pobijedili, bili smo dobro odigrali. Ali ono što sada vidim, jest jako dobra ekipa (Argentine), čini mi se snažnija nego prethodnih godina\", rekao je Modrić, koji je tada zabio gol za vodstvo od 2:0.\r\n<br><br>\r\n\"Stvorili su jaku ekipu s Messijem na čelu, a on je igrač koji stvara razliku. Sada je među njima veće zajedništvo, nisu izgubili puno utakmica, a to puno govori\", dodao je.\r\n<br><br>\r\nModrić, vezni igrač Real Madrida, u studenom bi u Kataru trebao odigrati svoje četvrto Svjetsko prvenstvo nakon Njemačke 2006., Brazila 2014. i Rusije 2018. godine.\r\n<br><br>\r\nUpitan postoji li razlika između europskih i južnoameričkih reprezentacija odgovorio da \"ne zna\".\r\n<br><br>\r\n\"Svaki kontinent ima svoj stil. Južna Amerika ima velike momčadi, s puno talenta. Kao i u Europi, svaka je utakmica izuzetno teška\", rekao je.\r\n<br><br>\r\nHrvatska je u finalu prošlog Svjetskog prvenstva bila poražena 2:4 od Francuske, a u ponedjeljak je u skupini Lige nacija pobijedila Francusku u Parizu s 1:0.\r\n<br><br>\r\nHrvatska će na Svjetskom prvenstvu igrati u skupini s Marokom, Kanadom i Belgijom dok će se Argentina suprotstaviti Saudijskoj Arabiji, Poljskoj i Meksiku.', 'art15.jpg', 'Sport', 0);

